function startsWithAny($string, $options)
{
    foreach ($options as $option) {
        if (strpos($string, $option) === 0) {
            return true;
        }
    }
    return false;
}

function isIpBlocked($ipToCheck, $blockedIps)
{
    foreach ($blockedIps as $blockedIp) {
        if ($ipToCheck === $blockedIp['ip']) {
            return true;
        }
    }
    return false;
}

